DROP TABLE IF EXISTS Researcher;CREATE TABLE Researcher(FirstName varchar(255) NOT NULL,LastName VARCHAR(255) NOT NULL,Job varchar(255) NOT NULL,Prefix varchar(20) NOT NULL,Suffix VARCHAR(20) NOT NULL,Phone VARCHAR (30),PhoneExtension varchar(30),Email varchar(255) NOT NULL,ORCID varchar(255) NOT NULL,PRIMARY KEY (ORCID));
DROP TABLE IF EXISTS Education;CREATE TABLE Education(ORCID varchar(255) NOT NULL,Degree varchar(255),Field varchar(255),Institution varchar(255),Location varchar(255),Year int);
DROP TABLE IF EXISTS Employment;CREATE TABLE Employment(Company varchar(255),Location varchar(255),Years INT,ORCID varchar(255) NOT NULL);
DROP TABLE IF EXISTS Societies;CREATE TABLE Societies(StartDate DATE,EndDate DATE,Society VARCHAR(255),Membership VARCHAR(255),Status VARCHAR(20),ORCID VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS Awards;CREATE TABLE Awards(Year INT,AwardingBody VARCHAR(255),Details VARCHAR(255),TeamMember VARCHAR(255),ORCID VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS Funding;CREATE TABLE Funding(StartDate DATE,EndDate DATE,AmountFunding int,FundingBody VARCHAR(255),FundingProgramme VARCHAR(255),Stats VARCHAR(255),PrimaryAttribution VARCHAR(255),ORCID VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS TeamMembers;CREATE TABLE TeamMembers(StartDate DATE,DepartureDate DATE,Name VARCHAR(255),Position VARCHAR(255),PrimaryAttribution VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS Impacts;CREATE TABLE Impacts(Title VARCHAR(255),Category VARCHAR(255),PrimaryBeneficiary VARCHAR(255) NOT NULL,PrimaryAttribution VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS InnovationAndCommercialisation;CREATE TABLE InnovationAndCommercialisation(Year int,Type VARCHAR(255),Title VARCHAR(255),PrimaryAttribution VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS Publications;CREATE TABLE Publications(Year int,Type VARCHAR(255),Title VARCHAR(255),Name VARCHAR(255),Status VARCHAR(255),DOI VARCHAR(255) NOT NULL, PrimaryAttribution VARCHAR(255) NOT NULL,PRIMARY KEY (DOI));
DROP TABLE IF EXISTS Presentations;CREATE TABLE Presentations(Year int,Title VARCHAR(255),Type VARCHAR(255),Conference VARCHAR(255),InvitedSeminar VARCHAR(255),Keynote VARCHAR(255),OrganisingBody VARCHAR(255),Location VARCHAR(255),PrimaryAttribution VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS Collaborations;CREATE TABLE Collaborations(StartDate DATE,EndDate DATE,Institution VARCHAR(255),Department VARCHAR(255),Location VARCHAR(255),NameCollaborator VARCHAR(255),PrimaryGoal VARCHAR(255),FrequencyOfInteraction VARCHAR(255),PrimaryAttribution VARCHAR(255) NOT NULL, Academic Boolean);
DROP TABLE IF EXISTS OrganisedEvents;CREATE TABLE OrganisedEvents(StartDate DATE,EndDate DATE,Title VARCHAR(255),Type VARCHAR(255),Role VARCHAR(255),Location VARCHAR(255),PrimaryAttribution VARCHAR(255) NOT NULL);
DROP TABLE IF EXISTS EducationAndPublicEngagement;CREATE TABLE EducationAndPublicEngagement(Name VARCHAR(255),StartDate DATE,EndDate DATE,Activity VARCHAR(255),Topic VARCHAR(255),TargetArea VARCHAR(255),PrimaryAttribution VARCHAR(255) NOT NULL);
